//Set Colours on Tables to Red Green or Amber

$(document).on('knack-view-render.view_150', function(event, view, data) {
   
    $("#view_150 td.field_130").each(function() {  
     
      
      var textColor = ($(this).find("span").text().trim() == "Red") ? "#ff0000" : "#0033CC"; 
      
      if ($(this).find("span").text().trim() != "Red"){
        if($(this).find("span").text().trim() == "Amber"){
           textColor = "#ff9900"; 
       }
        else{
           textColor = "#33CC00";
       }
    }
   
      
       
      
        $(this).css("background-color", textColor);  
   
    })
  
});

